    public function run($id='')
    {
        // 本地
        $local = \DB::table('bank_ncg')->select('code', 'name')->get();

        $other = '[{"bank_name":"BAAC","bank_code":"BAAC"},{"bank_name":"BAY","bank_code":"BAY"},{"bank_name":"BBL","bank_code":"BBL"},{"bank_name":"CIMB","bank_code":"CIMB"},{"bank_name":"CITI","bank_code":"CITI"},{"bank_name":"DB","bank_code":"DB"},{"bank_name":"GHB","bank_code":"GHB"},{"bank_name":"GSB","bank_code":"GSB"},{"bank_name":"HSBC","bank_code":"HSBC"},{"bank_name":"ICBC","bank_code":"ICBC"},{"bank_name":"ISBT","bank_code":"ISBT"},{"bank_name":"KBANK","bank_code":"KBANK"},{"bank_name":"KK","bank_code":"KK"},{"bank_name":"Kasikornbank Public CompanyLimited","bank_code":"KKB"},{"bank_name":"KTB","bank_code":"KTB"},{"bank_name":"LHBANK","bank_code":"LHBANK"},{"bank_name":"MHCB","bank_code":"MHCB"},{"bank_name":"SCB","bank_code":"SCB"},{"bank_name":"SCBT","bank_code":"SCBT"},{"bank_name":"SMBC","bank_code":"SMBC"},{"bank_name":"TBANK","bank_code":"TBANK"},{"bank_name":"TBNK","bank_code":"TBNK"},{"bank_name":"TCRB","bank_code":"TCRB"},{"bank_name":"TISCO","bank_code":"TISCO"},{"bank_name":"TMB","bank_code":"TMB"},{"bank_name":"TTB","bank_code":"TTB"},{"bank_name":"UOB","bank_code":"UOB"},{"bank_name":"UOBT","bank_code":"UOBT"}]';
        $other = json_decode($other, true);
        $arr = [];

        foreach ($other as $v) {
            $arr[$v['bank_code']] = $v['bank_name'];
        }

        $other = $arr;

        // 三方
        $json = [];
        $arr = [];
        foreach ($local as $lv) {

            $name = trim($lv->name);
            $names = strtoupper($name);
            $names = str_replace(' ', '', $names);

            $lv = trim($lv->code);
            $lvs = (strtoupper($lv));
            foreach ($other as $ok => $ov) {
                $ok = trim($ok);
                $oks = strtoupper($ok);
                $oks = str_replace(' ', '', $oks);

                $ov = trim($ov);
                $ovs = (strtoupper($ov));
                $ovs = str_replace(' ', '', $ovs);

                $iovs = $ovs.',INC.';

                if ($names == $iovs) {

                    $json[$lv] = $lv;
                    $arr[$lv] = $ok;
                    break;
                }

                if ($names == $oks) {
                    $json[$lv] = $lv;
                    $arr[$lv] = $ok;
                    break;
                }

                if ($names == $ovs) {
                    $json[$lv] = $lv;
                    $arr[$lv] = $ok;
                    break;
                }

                if ($lvs == $oks) {
                    $json[$lv] = $lv;
                    $arr[$lv] = $ok;
                    break;
                }

                if ($lvs == $ovs) {
                    $json[$lv] = $lv;
                    $arr[$lv] = $ok;
                    break;
                }
            }
        }

        echo '<pre>';
        echo 'json:', '<br>';
        $json = json_encode($json);
        $json = str_replace('"', '\"', $json);
        echo $json;

        echo '<hr>';
        foreach ($arr as $key => $value) {
            echo '"' . $key . '" => "' . $value . '",', '<br>';
        }

        exit();
        echo $linkpay;exit;
    }

