#CG游戏注单表
CREATE TABLE `game_order_cg`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `user_id` int(10) UNSIGNED NOT NULL COMMENT '用户ID',
  `SerialNumber` varchar(50) NOT NULL COMMENT '单号',
  `GameType` varchar(20) NOT NULL COMMENT '游戏类型',
  `LogTime` datetime NULL COMMENT '记录时间',
  `BetMoney` decimal(10, 2) NULL DEFAULT 0.00 COMMENT '总下注，单位为现金点数',
  `MoneyWin` decimal(10, 2) NULL DEFAULT 0.00 COMMENT '总得分=主游戏得分+小游戏得\r\n分，单位为现金点数',
  `NormalWin` idecimal(10, 2) NULL DEFAULT 0.00 COMMENT '主游戏得分，单位为现金点数',
  `BonusWin` decimal(10, 2) NULL DEFAULT 0.00 COMMENT '小游戏得分，单位为现金点数',
  `JackpotMoney` decimal(10, 2) NULL DEFAULT 0.00 COMMENT 'Jp 得分，单位为现金点数',
  `ThirdPartyAccount` varchar(20) NULL COMMENT '平台端帐号',
  `ValidBet` decimal(10, 2) NULL DEFAULT 0.00 COMMENT '有效投注',
  `Device` varchar(20) NULL COMMENT '用戶裝置',
  `IPAddress` varchar(20) NULL COMMENT '用戶 IP',
  PRIMARY KEY (`id`),
  UNIQUE INDEX `order_number`(`SerialNumber`) USING BTREE,
  INDEX `user_id`(`user_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = 'CG注单';

