

ALTER TABLE `user_dml` ADD COLUMN `TCG` int(10) UNSIGNED NOT NULL DEFAULT 0 AFTER `EVORT`;

DROP TABLE IF EXISTS `game_order_tcg`;
CREATE TABLE `game_order_tcg`  (
       `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '自增id',
       `user_id` int(30) NULL DEFAULT NULL COMMENT '用户id',
       `betAmount` decimal(10, 2) UNSIGNED NOT NULL COMMENT '投注金额',
       `actualBetAmount` decimal(10, 2) UNSIGNED NOT NULL COMMENT '实际投注金额',
       `orderNum` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '投注订单号',
       `gameCode` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '游戏代码',
       `device` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '使用设备：网页：WEB; 手机：MTH',
       `betNum` int(10) UNSIGNED NOT NULL COMMENT '注数',
       `winAmount` decimal(10, 2) NOT NULL COMMENT '中奖金额',
       `netPNL` decimal(10, 2) NOT NULL COMMENT '净输赢',
       `betStatus` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '订单状态(1:已中奖｜2:未中奖 ｜3:取消 | 4:和)',
       `username` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '玩家帐号',
       `numero` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '期号',
       `remark` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '彩种',
       `bettingContent` tinytext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '投注实际内容',
       `playBonus` decimal(10, 2) UNSIGNED NOT NULL COMMENT '赔率',
       `winningNumber` tinytext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '开奖号码',
       `playName` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '玩法名称',
       `gameGroupName` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '彩种组',
       `multiple` int(10) NOT NULL COMMENT '下注倍数',
       `playId` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '玩法ID',
       `betTime` datetime(0) NOT NULL COMMENT '投注时间',
       `settlementTime` datetime(0) NOT NULL COMMENT '结算时间',
       PRIMARY KEY (`id`) USING BTREE,
       UNIQUE INDEX `uniq_orderNo`(`orderNum`) USING BTREE,
       INDEX `idx_created`(`betTime`) USING BTREE,
       INDEX `idx_pc`(`username`, `betTime`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;

SET FOREIGN_KEY_CHECKS = 1;